	function thegov_fonts_url() {
		$fonts_url = '';
			
		
		/* Translators: If there are characters in your language that are not
		* supported by Quicksand, translate this to 'off'. Do not translate
		* into your own language.
		*/
		$quicksand = esc_html_x( 'on', 'Quicksand font: on or off', 'thegov' );	

		/* Translators: If there are characters in your language that are not
		* supported by Nunito, translate this to 'off'. Do not translate
		* into your own language.
		*/
		$nunito = esc_html_x( 'on', 'Nunito font: on or off', 'thegov' );

		if ('off' !== $quicksand || 'off' !== $nunito || 'off' !== $muli) {
			$font_families = array();

			if ( 'off' !== $quicksand ) {
				$font_families[] = 'Quicksand:400,500,600,700';
			}
			if ( 'off' !== $nunito ) {
				$font_families[] = 'Nunito Sans:400,600,700';
			}
				
			$query_args = array(
				'family' => urlencode( implode( '|', $font_families ) ),
				'subset' => urlencode( 'latin,latin-ext' ),
			);
				
			$fonts_url = add_query_arg( $query_args, 'https://fonts.googleapis.com/css' );
		}
		
		return esc_url_raw( $fonts_url );
	}

	function editor_google_fonts() {
		wp_enqueue_style( 'editor-google-fonts', thegov_fonts_url(), array(), '1.0.0' );
	}

	add_action( 'admin_enqueue_scripts', 'editor_google_fonts' );

	if (  !class_exists( 'Thegov_Core_Public' ) ) {

		function thegov_default_fonts() {
			wp_enqueue_style( 'thegov-default-font', thegov_fonts_url(), array(), '1.0.0' );
		}
		
		add_action( 'wp_enqueue_scripts', 'thegov_default_fonts' );

    }

